import React, { useState } from 'react';

function Status() {
  const [unique_id, setUnique_id] = useState('');
  const [data, setData] = useState(null);

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch(`localhost:4000//status/${unique_id}`);
      const json = await response.json();
      setData(json);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          unique_id:
          <input type="text" value={unique_id} onChange={(event) => setUnique_id(event.target.value)} />
        </label>
        <button type="submit">Submit</button>
      </form>
      {data && (
        <div>
          <h2>Data for {unique_id}:</h2>
          <p>{data.status}</p>
        </div>
      )}
    </div>
  );
}

export default Status;
