import React, { useEffect, useState } from "react";
// import FormStep1 from "./FormStep1";
// import FormStep2 from "./FormStep2";

export const ApplyForm = () => {
  const [form, setForm] = useState({
    first_name: "", last_name: "", email: "", phone: ""
  });
   const [error, setErrors] = useState(false)


  let name, value;
  const handleInput = (e) => {
    console.log(e)
    name = e.target.name
    value = e.target.value

    setForm({ ...form, [name]: value })
  }

  const postData = async (e) => {
    e.preventDefault()

    const { first_name, last_name, email, phone } = form

    if(first_name.length==0 || last_name.length == 0){
      set
    }


// if(!form.first_name.trim()){
//   errors.first_name = "First name is required";
// }else {
//   errors.first_name = ""
// }
// if(!form.last_name.trim()){
//   errors.last_name = "Last name is required"
// } else {
//   errors.last_name = ""
// }

    // email validation
    // const emailCond =
    //   "/^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:.[a-zA-Z0-9-]+)*$/";
    // if (!inputValues.email.trim()) {
    //   errors.email = 'Email is required';
    // } else if (!inputValues.email.match(emailCond)) {
    //   errors.email = 'Please ingress a valid email address';
    // } else {
    //   errors.email = '';
    // }

    //password validation
    // const cond1 = '/^(?=.*[a-z]).{6,20}$/';
    // const cond2 = '/^(?=.*[A-Z]).{6,20}$/';
    // const cond3 = '/^(?=.*[0-9]).{6,20}$/';
    // const password = inputValues.password;
    // if (!password) {
    //   errors.password = 'password is required';
    // } else if (password.length < 6) {
    //   errors.password = 'Password must be longer than 6 characters';
    // } else if (password.length >= 20) {
    //   errors.password = 'Password must shorter than 20 characters';
    // } else if (!password.match(cond1)) {
    //   errors.password = 'Password must contain at least one lowercase';
    // } else if (!password.match(cond2)) {
    //   errors.password = 'Password must contain at least one capital letter';
    // } else if (!password.match(cond3)) {
    //   errors.password = 'Password must contain at least a number';
    // } else {
    //   errors.password = '';
    // }




    const res = await fetch("/ApplyForm", {
      method: "POST",
      headers: {

        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        first_name, last_name, email, phone
      })
    });

    const data = await res.json()

    if (data.status === 400 || !data) {
      window.alert("Invalid Registration")
      console.log("invalid registration")
    } else {
      window.alert("Registration successfull")
      console.log("successfull registration")
    }

   
  }
  return (

    <div className="background-image-animation">
      <div className="Apply-container">
        <h1 id="Apply-For-Visa"> E-VISA APPLICATION </h1>
        <form method="POST">
        <div>
      <label>
          Application Type :
          <select>
            <option> Select... </option>
            <option> NORMAL PROCESSING (PROCESSING TIME 1 TO 4 BUSINESS DAYS) </option>
          </select>
        </label>
        </div>
        <br /> 
        <div>
        <label>
          Enter First Name :
          <input
            placeholder="First Name"
            type="string"
            name="first_name"
            onChange={handleInput}
            value={form.first_name}

            />

        </label>
        </div>

        <br />
        <div>
        <label>
          Enter Last Name :
          <input
            type="last_name"
            name="last_name"
            value={form.last_name}
            onChange={handleInput}
            placeholder="Last Name" />
        </label>
        </div>
        <br />
        <div>
        <label>
          Enter Email &nbsp; &nbsp; &nbsp; &nbsp; :
          <input
            type="email"
            name="email"
            value={form.email}
            onChange={handleInput}
            placeholder="email" />
        </label>
        </div>
        <br/>
        <div>
        <label>
          Enter Phone No  :
          <input
            type="text"
            name="phone"
            value={form.phone}
            onChange={handleInput}
            placeholder="Phone No" />
        </label>
        </div>
        <br/>
        <div>
        <label>
          Gender &nbsp; &nbsp; &nbsp; :
          <input type="radio" name="gender" /> Male
          <input type="radio" name="gender" /> Female
          <input type="radio" name="gender" /> Other
        </label>
        </div>
        <br />
        <div>
        <label>
          Date Of Birth &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;:
          <input type="date" />
        </label>
        </div>
        <br/>
        <div>
        <label>
        &nbsp; Expected Date of Journey :
          <input type="date" />
        </label>
        </div>
        <br />
        <div>
        <label>
          Nationality &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;:
          <select>
            <option> SELECT </option>
            <option> COLOMBIA </option>
            <option> CHINA </option>
            <option> INDONESIA </option>
          </select>
        </label>
        </div>
        <br />
        <div>
        <label>
          Passport Type &nbsp;&nbsp; &nbsp;:
          <select>
            <option> ORDINARY PASSPORT </option>
          </select>
        </label>
        </div>
        <br/>
        <div>
        <label>
          Port of Arrival &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;:
          <select>
          <option> SELECT </option>
            <option> DELHI </option>
