import React, { useState } from "react";
import { Field, useFormik } from "formik"
import { ApplySchema } from "../Schemas";

const initialValues = {
  first_name: "",
  last_name: "",
  email: "",
  phone: "",
  gender: "",
  date_of_birth: "",
  expected_date_of_journey: "",
  nationality: "",
  passport_type: "",
  port_of_arrival: ""
}

const options = [
  { value: '', label: "--SELECT--" },
  { value: 2, text: "Mumbai" },
  { value: 3, text: "Banglore" },
];


const ApplyForm = () => {
  const [selected, setSelected] = useState(options[0].value);

  const handleChangeSelect = (event) => {
    console.log(event.target.value);
    setSelected(event.target.value);
  };

  const { values, errors, touched, handleBlur, handleChange, handleSubmit } = useFormik({

    initialValues: initialValues,
    validationSchema: ApplySchema,
    onSubmit: (values) => {
      console.log(
        " ~ file: ApplyForm.jsx ~ line 11 ~ applyy ~ values",
        values
      );
    },
  });
  console.log(
    " ~ file: ApplyForm.jsx ~ line 11 ~ applyy ~ values",
    errors
  );



  const postData = async (e) => {
    e.preventDefault()

    const {
      first_name, last_name, email, phone, gender,
      date_of_birth, expected_date_of_journey,
      nationality, passport_type, port_of_arrival } = values

    const res = await fetch("/ApplyForm", {
      method: "POST",
      headers: {

        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        first_name, last_name, email, phone, gender,
        date_of_birth, expected_date_of_journey,
        nationality, passport_type, port_of_arrival
      })
    });

    const data = await res.json()

    if (data.status === false || !data) {
      window.alert(`Invalid Registration ${data.msg}`)
      console.log("invalid registration")
    } else {
      window.alert("Registration successfull")
      console.log("successfull registration")
    }
  }
  return (

    <div className="background-image-animation">
      <div className="Apply-container">
        <h1 id="Apply-For-Visa"> E-VISA APPLICATION </h1>
        <form method="POST">
          <div>
            <label>
              Application Type :
              <select>
                <option> Select... </option>
                <option> NORMAL PROCESSING (PROCESSING TIME 1 TO 4 BUSINESS DAYS) </option>
              </select>
            </label>
          </div>
          <br />
          <div>
            <label>
              Enter First Name :

              <input
                type="first_name"
                name="first_name"
                onChange={handleChange}
                value={values.first_name}
                onBlur={handleBlur}
                placeholder="First Name"
              />
              {errors.first_name && touched.first_name ? (
                <p id="form-error">{errors.first_name} </p>
              ) : null}
            </label>
          </div>

          <br />
          <div>
            <label>
              Enter Last Name :
              <input
                type="last_name"
                name="last_name"
                onChange={handleChange}
                value={values.last_name}
                onBlur={handleBlur}
                placeholder="Last Name" />
              {errors.last_name && touched.last_name ? (
                <p id="form-error">{errors.last_name} </p>
              ) : null}
            </label>
          </div>
          <br />
          <div>
            <label>
              Enter Email &nbsp; &nbsp; &nbsp; &nbsp; :
              <input
                type="email"
                name="email"
                onChange={handleChange}
                value={values.email}
                onBlur={handleBlur}
                placeholder="email" />
              {errors.email && touched.email ? (
                <p id="form-error">{errors.email} </p>
              ) : null}
            </label>
          </div>
          <br />
          <div>
            <label>
              Enter Phone No  :
              <input
                type="phone"
                name="phone"
                onChange={handleChange}
                value={values.phone}
                onBlur={handleBlur}
                placeholder="Phone No" />
              {errors.phone && touched.phone ? (
                <p id="form-error">{errors.phone} </p>
              ) : null}
            </label>
          </div>
          <br />
          <div
            onChange={handleChange}
            onBlur={handleBlur}
            value={values.gender}
          >
            <label>
              Gender &nbsp; &nbsp; &nbsp; :
              <input type="radio" name="gender" value="male" /> Male
              <input type="radio" name="gender" value="female" /> Female
              <input type="radio" name="gender" value="other" /> Other
              {errors.gender && touched.gender ? (
                <p id="form-error">{errors.gender} </p>
              ) : null}
            </label>
          </div>
          <br />
          {/*           
           <div>
            <label>
              Date Of Birth &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;:
              <input type="date"  
              onChange={handleChange} 
              onBlur={handleBlur}
              value ={values.date_of_birth}
              />
            </label>
          </div>
          <br />
          <div>
            <label>
              &nbsp; Expected Date of Journey :
              <input type="date" 
              onChange={handleChange} 
              onBlur={handleBlur}
              value ={values.expected_date_of_journey}
              />
            </label>
          </div>
          <br />
          <div>
            <label>
              Nationality &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;:
              <select 
               onChange={handleChange}
               onBlur={handleBlur}
               value={values.nationality} >
                <option> SELECT </option>
                <option> COLOMBIA </option>
                <option> CHINA </option>
                <option> INDONESIA </option>
              </select>
            </label>
          </div>
          <br />
          <div>
            <label>
              Passport Type &nbsp;&nbsp; &nbsp;:
              <select 
              onChange={handleChange}
               onBlur={handleBlur}
               value={values.passport_type}>
                <option> ORDINARY PASSPORT </option>
              </select>
            </label>
          </div>
          <br />
           

          <div>
            <label>
              Port of Arrival &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;:
              <select 
              onChange={handleChange} 
              onBlur={handleBlur}
               value={values.port_of_arrival}
            >
                <option name= "port_of_arrival" value={values.port_of_arrival} > SELECT </option>
                <option name= "port_of_arrival" value={values.port_of_arrival} > Delhi </option>
                <option name= "port_of_arrival" value={values.port_of_arrival}> Mumbai </option>
                {/* <option name="port_of_arrival" value=""  > KOLKATA </option>
                <option name="port_of_arrival" value=""  > RAJASTHAN </option>
                <option name="port_of_arrival" value= "" > BANGLORE </option>
                <option name="port_of_arrival" value= "" > KARNATAKA </option>
                <option name="port_of_arrival" value=""  > TAMILNADU </option>  
              </select>
            </label>
          </div>
 
*/}

          {/*              
        <div> 
   
        <Field  component="select" name="port_of_arrival" placeholder="Select options" >
        
         <option value="Delhi"> Delhi </option>
         <option value="Mumbai"> Mumbai </option>
         </Field>

        </div> */}
          <div>
            <select value={selected} onChange={handleChangeSelect}>
              {options.map(option => (
                <option key={option.value} value={option.value}>
                  {option.text}
                </option>
              ))}
            </select>
          </div>

          <br />
          <div>
            <label>
              Checkbox &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  &nbsp; &nbsp; &nbsp;:
              <input type="checkbox" />abc
              <input type="checkbox" />abc
              <input type="checkbox" />abc
            </label>
          </div>
          <br />

          <input type="submit" name="submit-form" id="submit-button" className="form-submit"

            value="Submit" onClick={postData} />

        </form>
      </div>
    </div>
  )
}


export default ApplyForm
