import React from "react";
import { useState } from "react";

const ApplyForm = () => {
  const [form, setForm] = useState({
     first_name : "", last_name : "",
  });

  let name, value;

  const  handleInput = (e) => {
   console.log(e)
   name = e.target.name
   value = e.target.value

   setForm({...form,[name]:value})
     
  }

  const postData = async (e) => {
      e.preventDefault()

      const {first_name,last_name} = form

     const res = await fetch("/ApplyForm", {
        method: "POST",
        headers:{

          "Content-Type" : "application/json"
        },
        body: JSON.stringify({
          first_name, last_name
        })
      });

      const data = await res.json()
      
      if(data.status === 400 || !data ){
        window.alert("Invalid Registration")
        console.log("invalid registration")
      } else {
        window.alert("Registration successfull")
        console.log("successfull registration")
      }
  }
  return (
    <div className="Apply-container">
      <h1 id="Apply-For-Visa"> APPLY FOR VISA </h1>
      <form method="POST">
        <label>
          Enter First Name :
          <input
            type="first_name"
            name = "first_name"
            value={form.first_name}
            onChange={handleInput}
            placeholder="First Name" />
        </label>
        <br />
        <label>
          Enter Last Name :
          <input
            type="last_name"
            name = "last_name"
            value={form.last_name}
            onChange={handleInput}
            placeholder="Last Name" />
        </label>
        <br />
        <label>
        Gender :
        <input type="radio" name="gender"/> Male
        <input type="radio" name="gender"/> Female
        <input type="radio" name="gender"/> Other
        </label>
        <br />
        <label>
          Expected Date of Journey :
          <input type="date"/>
        </label>
        <br />
        <label>
         Nationality
          <select>
            <option> select </option>
            <option> INDIA </option>
            <option> China </option>
            <option> Indonesia </option>
          </select>
        </label>
        <br />
        <br />
        <label>
          <input/>
        </label>
        <br />
        {/* <div className="radion-button">
        Gender :
        
        <label><input type="radio" name="gender" /> Male </label>
        <br/>
        <label><input type="radio" name="gender" /> Female </label>
        <br/>
        <label><input type="radio" name="gender" /> other </label>
        <br/>
        </div> */}
        <input type="submit" name="submit-form" id="submit-button" className="form-submit"
          value="submit" onClick={postData}
          //--------------------------------------------- local storage
        />
        {/* <button type="submit"> Submit </button> */}
        {/* <button type="submit" name="save" value="save">Save</button> */}
      </form>
    </div>
  )
}
export default ApplyForm
