import React from "react";
import { useState } from "react";
import FormStep1 from "./FormStep1";

const ApplyForm = () => {
  const [form, setForm] = useState({
    first_name: "", last_name: "", email: "", phone: ""
  });

  let name, value;

  const handleInput = (e) => {
    console.log(e)
    name = e.target.name
    value = e.target.value

    setForm({ ...form, [name]: value })

  }

  const postData = async (e) => {
    e.preventDefault()

    const { first_name, last_name , email , phone} = form

    const res = await fetch("/ApplyForm", {
      method: "POST",
      headers: {

        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        first_name, last_name , email , phone
      })
    });

    const data = await res.json()

    if (data.status === 400 || !data) {
      window.alert("Invalid Registration")
      console.log("invalid registration")
    } else {
      window.alert("Registration successfull")
      console.log("successfull registration")
    }
  }
  return (
    
    <div className="background-image-animation">
    <div className="Apply-container">
      <h1 id="Apply-For-Visa"> E-VISA APPLICATION </h1>
      <form method="POST">
      <div>
      <label>
          Application Type :
          <select>
            <option> Select... </option>
            <option> NORMAL PROCESSING (PROCESSING TIME 1 TO 4 BUSINESS DAYS) </option>
          </select>
        </label>
        </div>
        <br /> 
        <div>
        <label>
          Enter First Name :
          <input
            type="first_name"
            name="first_name"
            value={form.first_name}
            onChange={handleInput}
            placeholder="First Name" 
            />
        </label>
        </div>
        <br />
        <div>
        <label>
          Enter Last Name :
          <input
            type="last_name"
            name="last_name"
            value={form.last_name}
            onChange={handleInput}
            placeholder="Last Name" />
        </label>
        </div>
        <br />
        <div>
        <label>
          Enter Email :
          <input
            type="email"
            name="email"
            value={form.email}
            onChange={handleInput}
            placeholder="email" />
        </label>
        </div>
        <br/>
        <div>
        <label>
          Enter Phone No :
          <input
            type="text"
            name="phone"
            value={form.phone}
            onChange={handleInput}
            placeholder="Phone No" />
        </label>
        </div>
        <FormStep1/>
f
        <input type="submit" name="submit-form" id="submit-button" className="form-submit"
          value="Submit And Continue" onClick={postData} />

      </form>
    </div>
    </div>
  )
}
export default ApplyForm
